mapper.update:
UPDATE USER
user_password = #{userPassword},
user_email = #{newEmail},
user_name = #{userName}, user_rep_name = #{userRepName}, user_birth = #{userBirth}, user_phone_number = #{userPhoneNumber}, user_phone_number_sub = #{userPhoneNumberSub}, user_postcode = #{userPostcode}, user_address1 = #{userAddress1}, user_address2 = #{userAddress2}
WHERE user_id = #{userId}
