mapper.update:
UPDATE USER SET user_name = #{userName}, user_password = #{userPassword},
user_email = #{newEmail},
user_phone_number = #{userPhoneNumber}, user_postcode = #{userPostcode}, user_address1 = #{userAddress1}, user_address2 = #{userAddress2} WHERE user_id = #{userId}

mapper.select:
SELECT *
FROM 
	RESERVATION
WHERE
	user_idx = #{user.userIdx}
ORDER BY 
	(reservation_status = 1) DESC, reservation_com
LIMIT
	#{startRow}
	, #{listLimit}

mapper.update:
UPDATE RESERVATION
set
	reservation_class_date = #{reservationClassDate}
	, reservation_members = #{reservationMembers}
WHERE
	reservation_idx = #{reservationIdx}

mapper.select:
SELECT COUNT(*)
FROM RESERVATION
WHERE user_idx = #{userIdx}

SELECT *
FROM INQUERY
WHERE user_idx = #{user.userIdx}
	AND inquery_type = 1
ORDER BY inquery_status, inquery_datetime DESC
LIMIT
	#{startRow}
	, #{listLimit}

mapper.update:
UPDATE INQUERY
SET
	inquery_title = #{inqueryTitle}
	, inquery_detail = #{inqueryDetail}
	, inquery_modify_datetime = now()
WHERE
	inquery_idx = #{inqueryIdx}

mapper.select:
SELECT I.*, C.class_title
FROM INQUERY I
JOIN CLASS C ON I.class_idx = C.class_idx
WHERE I.user_idx = #{user.userIdx}
	AND I.inquery_type = 2
ORDER BY I.inquery_status, I.inquery_datetime DESC
LIMIT
	#{startRow}
	, #{listLimit}
